var minWindow = function (s, t) {
  // Check if s has enough letters to contain t
  if (s.length < t.length) return ''

  let sArr = s.split('')
  let minWord = ''

  tArr = t.split('')
  for (let i = 0; i <= sArr.length - tArr.length; i++) {
    tArr = t.split('').sort()
    let wordArr = []

    // If letter is not a target, move on
    if (!tArr.includes(sArr[i])) {
      continue
    }

    for (let end = i; end < sArr.length; end++) {
      let letter = s[end]
      // console.log('Current letter: ' + letter)

      wordArr.push(letter)

      // If letter is not a target, move on
      if (!tArr.includes(letter)) {
        continue
      }

      // Remove from Array
      tArr.splice(tArr.indexOf(letter), 1)

      // If target array is now empty, we check if this is the smallest Word
      if (tArr.length == 0) {
        // let word = wordArr.join('')

        if (minWord == '') {
          minWord = wordArr.join('')
        } else {
          minWord = wordArr.length < minWord.length ? wordArr.join('') : minWord
        }

        console.log('word: ' + wordArr.join(''))

        if (minWord.length == t.length) return minWord

        break
      }
    }
  }

  if (minWord.length == 0) return ''

  return minWord
}

